tool.command:
matlab -r "
diary diary.txt; 
current_directory = pwd; 
cellorganizer_directory = getenv('CELLORGANIZER'); 
cd( cellorganizer_directory );
setup();
tic; 
cd( './demos/3D/');
[answer,directory] = system('uuidgen');
directory = strrep(directory,'-','');
directory = strtrim(directory);
system(['cp -rv ' pwd filesep 'demo3D42 ' directory]);
cd( directory );
demo3D42();
system(['cp -rv models ' current_directory]);
system(['cp -rv param ' current_directory]);
system(['cp -rv temp ' current_directory]);
cd ..;
rmdir(directory,'s');
cd(current_directory);
diary off;
pwd,
ls,
toc,
exit;";

bash $__tool_directory__/demo3D42.sh $output.extra_files_path;
